          {
            "policyName": "GroupManagement-US",
            "policyArn": "arn:aws:iot:ap-northeast-1:XXXXXXXXXXX:policy/GroupManagement-US"
          }
        ]
        }
      },
      {
       "authDecision": "IMPLICIT_DENY",
